import React from 'react';
import { useState, useEffect } from 'react';
import MealItem from './MealItem/MealItem';
import Card from '../UI/Card';
import classes from './AvailableMeals.module.css';


const AvailableMeals = () => {
    const [meals, setMeals] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [httpError, setHttpError] = useState();
    console.log('React.version ', React.version);

    useEffect(() => {
        const fetchMeals = async () => {
            const response = await fetch('https://max-complete-react-default-rtdb.europe-west1.firebasedatabase.app/meals.js');

            console.log('log 1 , reponse', response);
            if (!response.ok) {
                console.log('log 2 , response', error);

                throw new Error('Something went wrong');
            }
            console.log('log 3 , error', error);

            console.log('response ', response);
            const responseData = await response.json();

            const loadedMeals = [];

            for (const key in responseData) {
                loadedMeals.push({
                    id: key,
                    name: responseData[key].name,
                    description: responseData[key].description,
                    price: responseData[key].price
                });
            }

            setMeals(loadedMeals);
            setIsLoading(false);
        };
        try {
            fetchMeals();
        } catch ( error ) {
            console.log('error caught ', error)
            setIsLoading(false);
            setHttpError(error.message);
        }

        
    }, []);

    if (isLoading) {
        return (
            <section className={classes.mealsLoading}>
                <p>Content is Loading....</p>
            </section>
        );
    }
    if (httpError) {
        return (
            <section className={classes.mealsError}>
                <p>{httpError}</p>
            </section>
        );
    }
    const mealsList = meals.map((meal) =>
        <MealItem
            key={meal.id}
            id={meal.id}
            name={meal.name}
            price={meal.price}
            description={meal.description}>
        </MealItem>
    )

    return (
        <section className={classes.meals}>
            <Card>
                <ul>{mealsList}</ul>
            </Card>
        </section>
    )
};

export default AvailableMeals;
